mapper.insert:
INSERT INTO oyMember (
oymbName
, oymbNameEng
, oymbId
, oymbNickName
, oymbPassword
, oymbDelNy
) values (
#{oymbName}
, #{oymbNameEng}
, #{oymbId}
, #{oymbNickName}
, #{oymbPassword}
, 0
)

	SELECT last_insert_id()

INSERT INTO oymbJoinQna (
oyjqQuestionCd
, oyjqAnswer
, oyjqDelNy
, oymbSeq
) values (
#{oyjqQuestionCd}
,#{oyjqAnswer}
,0
,#{oymbSeq}
)

	SELECT last_insert_id()

INSERT INTO oyNationality (
oynaName
, oynaDelNy
) values (
#{oynaName}
,0
)

	SELECT last_insert_id()

INSERT INTO oymbNationality (
oymnDelNy
, oymbSeq
, oynaSeq
) values (
0
,#{oymbSeq}
,#{oynaSeq}
)

	SELECT last_insert_id()

INSERT INTO oymbPhone (
oympTelecomCd
, oympNumber 
, oympDelNy
, oymbSeq
) values (
#{oympTelecomCd}
,#{oympNumber}
,0
,#{oymbSeq}
)

	SELECT last_insert_id()

INSERT INTO oymbEmail (
oymeEmailFull
, oymeEmailAccount
, oymeEmailDomainCd
, oymeEmailDomain
, oymeDelNy
, oymbSeq
) values (
#{oymeEmailFull}
,#{oymeEmailAccount}
,#{oymeEmailDomainCd}
,#{oymeEmailDomain}
,0
,#{oymbSeq}
)

	SELECT last_insert_id()

mapper.select:
SELECT a.oymbSeq , a.oymbName , a.oymbNameEng , a.oymbId , a.oymbNickName , a.oymbPassword , a.oymbDelNy , b.oyjqQuestionCd , b.oyjqAnswer , b.oyjqDelNy , c.oymnSeq , c.oymnDelNy , d.oymaSeq , d.oymaDefaultNy , d.oymaTypeCd , d.oymaZipCode , d.oymaAddress1 , d.oymaAddress2 , d.oymaDelNy , e.oympSeq , e.oympDefaultNy , e.oympTypeCd , e.oympTelecomCd , e.oympNumber , e.oympDelNy , f.oymeSeq , f.oymeDefaultNy , f.oymeTypeCd , f.oymeEmailFull , f.oymeEmailAccount , f.oymeEmailDomain , f.oymeEmailDomainCd , f.oymeDelNy
FROM oyMember a LEFT JOIN oymbJoinQna b ON b.oymbSeq = a.oymbSeq LEFT JOIN oymbNationality c ON c.oymbSeq = a.oymbSeq LEFT JOIN oymbAddress d ON d.oymaDefaultNy=1 AND d.oymbSeq = a.oymbSeq LEFT JOIN oymbPhone e ON e.oympDefaultNy=1 AND e.oymbSeq = a.oymbSeq LEFT JOIN oymbEmail f ON f.oymeDefaultNy=1 AND f.oymbSeq = a.oymbSeq
WHERE 1=1 AND a.oymbSeq = #{oymbSeq}

SELECT oympSeq , oympDefaultNy , oympTypeCd , oympTelecomCd , oympNumber , oympDelNy
FROM oymbPhone
WHERE 1=1 AND oympDelNy = 0 AND oymbSeq = #{oymbSeq}

SELECT oymeSeq , oymeDefaultNy , oymeTypeCd , oymeEmailDomainCd , oymeEmailAccount , oymeEmailDomain , oymeEmailFull
FROM oymbEmail
WHERE 1=1 AND oymeDelNy = 0 AND oymbSeq = #{oymbSeq}

mapper.update:
UPDATE
oyMember
SET
oymbName = #{oymbName}
, oymbNameEng = #{oymbNameEng}
, oymbId = #{oymbId}
, oymbNickName = #{oymbNickName}
, oymbPassword = #{oymbPassword}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbJoinQna
SET
oyjqQuestionCd = #{oymbName}
, oyjqAnswer = #{oymbName}
, oyjqDelNy = #{oymbName}
, oymbSeq = #{oymbName}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oyNationality
SET
oynaName = #{oynaName}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbNationality
SET
oymnDelNy = #{oymnDelNy}
, oynaSeq = #{oynaSeq}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbPhone
SET
oympTelecomCd = #{oympTelecomCd}
, oympNumber = #{oympNumber}

WHERE 1=1
AND oymbSeq = #{oymbSeq}

UPDATE
oymbEmail
SET
oymeEmailFull = #{oymeEmailFull}
, oymeEmailAccount = #{oymeEmailAccount}
, oymeEmailDomainCd = #{oymeEmailDomainCd}
, oymeEmailDomain = #{oymeEmailDomain}

WHERE 1=1
AND oymbSeq = #{oymbSeq}
