mapper.update:
UPDATE
oyMember
SET
oymbName = #{oymbName}
, oymbNameEng
= #{oymbNameEng}
, oymbId = #{oymbId}
, oymbNickName = #{oymbNickName}
,
oymbPassword = #{oymbPassword}
, oymbGenderCd = #{oymbGenderCd}
,
oymbSkinTypeCd = #{oymbSkinTypeCd}
, oymbPersonalColorCd =
#{oymbPersonalColorCd}
, oymbInterestsCd = #{oymbInterestsCd}
,
oymbSmsConsentNy = #{oymbSmsConsentNy}
, oymbEmailConsentNy =
#{oymbEmailConsentNy}
, oymbPushConsentNy = #{oymbPushConsentNy}
,
oymbSavedCd = #{oymbSavedCd}

WHERE 1=1
AND oymbSeq
= #{oymbSeq}
