mapper.insert:
INSERT INTO oyMember (
oymbName
, oymbNameEng
, oymbId
, oymbNickName

, oymbPassword
, oymbGenderCd
, oymbDelNy
, regDateTime
, modDateTime
) values (
#{oymbName}
, #{oymbNameEng}
, #{oymbId}
, #{oymbNickName}

, #{oymbPassword}
, #{oymbGenderCd}
, 0
, now()
, now()
)

	SELECT last_insert_id()

INSERT INTO oymbJoinQna (
oyjqQuestionCd
, oyjqAnswer
, oyjqDelNy
, regDateTime
, modDateTime
, oymbSeq
) values (
#{oyjqQuestionCd}
,#{oyjqAnswer}
,0
, now()
, now()
,#{oymbSeq}
