mapper.insert:
INSERT INTO oyMember (
oymbId
, oymbName
, oymbDelNy
)
values (
#{oymbId}
,
#{oymbName}
, 0
)

mapper.select:
SELECT a.oymbSeq , a.oymbName , a.oymbId , a.oymbDob , a.oymbDelNy , a.oymbGenderCd ,(select oycdName from oyCode where oycdSeq = a.oymbGenderCd) as oymbGender , b.oyoaSnsTypeCd ,(select oycdName from oyCode where oycdSeq = b.oyoaSnsTypeCd) as oymbSns , b.oyoaUrl , b.oyoaDelNy , c.oyjqQuestionCd ,(select oycdName from oyCode where oycdSeq = c.oyjqQuestionCd) as oymbQuestion , c.oyjqAnswer , c.oyjqDelNy , d.oymaAddress1 , d.oymaAddress2 , d.oymaDelNy , e.oymnSeq ,(select oynaName from oyNationality where oynaSeq = e.oymbSeq) as oymbNationality , f.oympTelecomCd ,(select oycdName from oyCode where oycdSeq = f.oympTelecomCd) as oymbTelecom , f.oympNumber , f.oympDelNy , g.oymeEmailAccount , g.oymeEmailDomainCd ,(select oycdName from oyCode where oycdSeq = g.oymeEmailDomainCd) as oymbEmailDomain , g.oymeDelNy
FROM oyMember a LEFT JOIN oymbOnlineaddress b on b.oyoaDefaultNy=1 AND b.oymbSeq = a.oymbSeq LEFT JOIN oymbJoinqna c on c.oymbSeq = a.oymbSeq LEFT JOIN oymbAddress d on d.oymaDefaultNy=1 AND d.oymbSeq = a.oymbSeq LEFT JOIN oymbNationality e on e.oymbSeq = oynaSeq LEFT JOIN oymbPhone f on f.oympDefaultNy=1 AND f.oymbSeq = a.oymbSeq LEFT JOIN oymbEmail g on g.oymeDefaultNy=1 AND g.oymbSeq = a.oymbSeq
WHERE 1=1 AND a.oymbDelNy = 0 AND a.oymbSeq = #{oymbSeq}

mapper.update:
UPDATE
oyMember
SET
oymbName = #{oymbName}
, oymbId =
#{oymbId}

WHERE 1=1
AND oymbSeq = #{oymbSeq}
